import spock.lang.*class TestSample extends Spock4Js{  // 対象となるスクリプト  @Shared script = ('src/main/js/sample.js' as File).text    def "JavaScript Spock Sample"(){    expect: run('greet', name) == greet    where:    name     | greet    'Spock'  | 'Hello!! Spock'    'Rhino'  | 'Hello!! Spock'      }  def "two args function"(){  expect: run('twoArgs', [one, two]) == result    where:    one     | two     | result    1       | 2       | 3    'Spock' | 'Rhino' | 'SpockRhino'    2       | 3       | 4  }}